{
  "definitions": {},
  "$schema": "http://json-schema.org/draft-06/schema#",
  "$id": "https://beta.todoist.com/API/v8/projects",
  "type": "object",
  "title": "The Root Schema",
  "required": [
    "id",
    "name",
    "order",
    "indent",
    "comment_count"
  ],
  "properties": {
    "id": {
      "$id": "#/properties/id",
      "type": "integer",
      "title": "The Id Schema",
      "default": 0,
      "examples": [
        2196264843
      ]
    },
    "name": {
      "$id": "#/properties/name",
      "type": "string",
      "title": "The Name Schema",
      "default": "",
      "examples": [
        "Tsuzuki"
      ],
      "pattern": "^(.*)$"
    },
    "order": {
      "$id": "#/properties/order",
      "type": "integer",
      "title": "The Order Schema",
      "default": 0,
      "examples": [
        5
      ]
    },
    "indent": {
      "$id": "#/properties/indent",
      "type": "integer",
      "title": "The Indent Schema",
      "default": 0,
      "examples": [
        1
      ]
    },
    "comment_count": {
      "$id": "#/properties/comment_count",
      "type": "integer",
      "title": "The Comment_count Schema",
      "default": 0,
      "examples": [
        0
      ]
    }
  }
}

{
  "definitions": {},
  "$schema": "http://json-schema.org/draft-06/schema#",
  "$id": "https://beta.todoist.com/API/v8/tasks",
  "type": "object",
  "title": "The Root Schema",
  "required": [
    "id",
    "project_id",
    "content",
    "completed",
    "label_ids",
    "order",
    "indent",
    "priority",
    "comment_count",
    "due"
  ],
  "properties": {
    "id": {
      "$id": "#/properties/id",
      "type": "integer",
      "title": "The Id Schema",
      "default": 0,
      "examples": [
        2840843226
      ]
    },
    "project_id": {
      "$id": "#/properties/project_id",
      "type": "integer",
      "title": "The Project_id Schema",
      "default": 0,
      "examples": [
        2195347807
      ]
    },
    "content": {
      "$id": "#/properties/content",
      "type": "string",
      "title": "The Content Schema",
      "default": "",
      "examples": [
        "Appointment with Maria4"
      ],
      "pattern": "^(.*)$"
    },
    "completed": {
      "$id": "#/properties/completed",
      "type": "boolean",
      "title": "The Completed Schema",
      "default": false,
      "examples": [
        false
      ]
    },
    "label_ids": {
      "$id": "#/properties/label_ids",
      "type": "array",
      "title": "The Label_ids Schema"
    },
    "order": {
      "$id": "#/properties/order",
      "type": "integer",
      "title": "The Order Schema",
      "default": 0,
      "examples": [
        1
      ]
    },
    "indent": {
      "$id": "#/properties/indent",
      "type": "integer",
      "title": "The Indent Schema",
      "default": 0,
      "examples": [
        1
      ]
    },
    "priority": {
      "$id": "#/properties/priority",
      "type": "integer",
      "title": "The Priority Schema",
      "default": 0,
      "examples": [
        4
      ]
    },
    "comment_count": {
      "$id": "#/properties/comment_count",
      "type": "integer",
      "title": "The Comment_count Schema",
      "default": 0,
      "examples": [
        0
      ]
    },
    "due": {
      "$id": "#/properties/due",
      "type": "object",
      "title": "The Due Schema",
      "required": [
        "recurring",
        "string",
        "date",
        "datetime",
        "timezone"
      ],
      "properties": {
        "recurring": {
          "$id": "#/properties/due/properties/recurring",
          "type": "boolean",
          "title": "The Recurring Schema",
          "default": false,
          "examples": [
            false
          ]
        },
        "string": {
          "$id": "#/properties/due/properties/string",
          "type": "string",
          "title": "The String Schema",
          "default": "",
          "examples": [
            "tomorrow at 12:00"
          ],
          "pattern": "^(.*)$"
        },
        "date": {
          "$id": "#/properties/due/properties/date",
          "type": "string",
          "title": "The Date Schema",
          "default": "",
          "examples": [
            "2018-10-03"
          ],
          "pattern": "^(.*)$"
        },
        "datetime": {
          "$id": "#/properties/due/properties/datetime",
          "type": "string",
          "title": "The Datetime Schema",
          "default": "",
          "examples": [
            "2018-10-03T12:00:00Z"
          ],
          "pattern": "^(.*)$"
        },
        "timezone": {
          "$id": "#/properties/due/properties/timezone",
          "type": "string",
          "title": "The Timezone Schema",
          "default": "",
          "examples": [
            "UTC-05:00"
          ],
          "pattern": "^(.*)$"
        }
      }
    }
  }
}
